import { useState } from "react";
import Box from '@mui/material/Box';
import TextField from '@mui/material/TextField';

export function TodoList() {
    const [comment, setInputText] = useState("");
    const [arr, setArr] = useState([{ ID: 1, text: "Это первый комментарий" },
        { ID: 2, text: "Это второй комментарий" },
        { ID: 3, text: "Это третий комментарий" }]);
    const [DeleteComment, setDeleteComment] = useState([]);
    const [id, setId] = useState(1);

    function updateId() {
        setId(id + 1);
    }

    function Add(event) {
        event.preventDefault();
        if (!comment.trim()) {
            return;
        }
        setArr([...arr, { ID: id, text: comment }]);
        updateId();
        setInputText("");
    }

    const Delete = (ids) => {
        setDeleteComment((Deletes) => {
            return [...Deletes, ids];
        });
    }
   
    
  return (
    <Box
      component="form"
    //   sx={{ '& .MuiTextField-root': { m: 1, width: '25ch',height:'50ch' } }}
      noValidate
      autoComplete="off"
    >
    <div>
    <TextField
          required
          id="outlined-required"
          label="Required"
          defaultValue="Введите текст"
          onChange={(event) => setInputText(event.target.value)}
          value={comment}
        />
    

    <button onClick={Add}>Добавить комментарий</button>

    <ul>
        {arr.map((comment) => (
            <li key={comment.ID}>
                <p>{comment.text}</p>
            </li>
        ))}
    </ul>
</div>
</Box>
  )
}
